sys_script_client.script:
function onChange(control, oldValue, newValue, isLoading, isTemplate) {
   if (newValue === '') {
      return;
   }

	var whatneeded = g_form.getValue("u_what_needed");
	//^prende il valore del campo selezionato

	g_form.clearOptions("u_what_needed");
	//^pulisce le opzioni dei select del campo selezionato

	if (newValue == "hr") {
		g_form.addOption("u_what_needed", "hr1", "Human Resource 1");
		g_form.addOption("u_what_needed", "hr2", "Human Resource 2");
		g_form.addOption("u_what_needed", "other", "Other");
		//addOption ha tre valori: campo, condizione e il valore che devo inserire all'interno per sostituire le option della select in modo forzato
	}

	else if (newValue == "facilities"){
		g_form.addOption("u_what_needed", "facilities1", "facilities1");
		g_form.addOption("u_what_needed", "facilities2", "facilities2");
		g_form.addOption("u_what_needed", "other", "Other");	
	}

	else if (newValue == "legal"){
		g_form.addOption("u_what_needed", "legal1", "legal1");
		g_form.addOption("u_what_needed", "legal2", "legal2");
		g_form.addOption("u_what_needed", "other", "Other");	
	}

	else if (isLoading && !g_form.isNewRecord()){
		g_form.getValue("u_what_needed", whatneeded);
	}

   //Commenti sul codice: creiamo una variabile e la popoliamo con il valore che tiene "u_what_whatneeded" (nome della tabella che ci interessa). Con la successiva g.form cancelliamo le opzioni di u_what_needed. I valori dentro questa tabella sono "hr", "facilities" e "legal"; pertanto aggiungo tramite il g_form l'opzione che varia sulla base di quale valore sia (se hr, facilities o legal). Tutto ciò risulta vero finché ho un nuovo valore. Nel caso in cui non avessi nuovi valori, valorizzalo con la variabile che preventivamente ho raccolto (riga 6): ovvero i valori tornano uguali, perché per un momento vengono cambiati negli IF.
}
