var CompletamentoEmail = Class.create();
CompletamentoEmail.prototype = Object.extendsObject(global.AbstractAjaxProcessor, {

	getEmail: function() {
	var utilizzoRecord = new GlideRecord("sys_user");
	//^assegna alla variabile "utilizzoRecord" tutti i record presenti in "sys_user" (l'utente connesso)
	utilizzoRercord.get(this.getParameter("sysparm_userID"));
	//^dai record di "sys_user" prendimi tutti i parametre dell'utente ID connesso;
	return utilizzoRecord.email + "";
	//^stampami l'email specifica legata all'ID

},
    type: 'CompletamentoEmail'
	//^si riceve la tipologia che si possiede che risulta essere il nome stesso dello script. E' il modo che viene utilizzato per essere richiamato (completa lo script include). 
});
